import React from 'react';
import {Grid, Typography, makeStyles, Button, withStyles, IconButton, Avatar} from '@material-ui/core';
import logo from '../../Images/LOGO.png';
import m_Notation from '../../Images/icons8-music-notation-64.png';
import m_Album from "../../Images/icons8-music-album-60.png";

const useStyle = makeStyles((theme) => ({
    root:{
        position:"relative",
        width:'100%',
        height:'100vh !important',
        color:'white',
        background:'#060F1E',
        '& .sideBar':{
            position:"relative",
            height:"100%",
            width:"21%",
            boxSizing:"border-box",
            float:"left"
        },
        '& .contentCarrier':{
            position:"relative",
            height:"94%",
            width:"75%",
            boxSizing:"border-box",
            float:"right",
            top:"3%",
            left:"2%",
            borderRadius:"2.5vh",
            background:"#06142B"
        },
        '& .sideTab':{
            position:"relative",
            height:"90%",
            width:"92%",
            top:"5%",
            left:"4%",
            margin:"0",

        },
        '& .logo':{
            position:"relative",
            width:"85%",
            left:"15%",
            height:"7%",
            backgroundImage:`url(${logo})`,
            backgroundSize:'contain',
            backgroundPosition:'left',
            backgroundRepeat:'no-repeat'
        },
        '& .navLinks1':{
            position:"relative",
            width:"75%",
            left:"15%",
            height:"60%"
        },



    }
}));

const Btn = withStyles({
    root:{
        background:"transparent",
        color:"white",
        cursor:"pointer",
        borderRadius:"10",
        border:'0',
        height:"50",
        padding:"6px 8px"
    },
    label:{
        textTransform:"none",
        justifyContent:"left",
        fontFamily:"Ubuntu Bold"
    },
    startIcon:{
        marginLeft:"6px",
        marginRight:"2px"
    }

})(props => <Button {...props} />);

const Home = () => {

    const classes = useStyle();
    return (
       <div className={classes.root}> 
           <div style={{position:"relative", width:'100%',height:'100%',display:"flex"}}>
                <Grid className="sideBar">
                    <Grid className="sideTab">
                        <div className="logo">
                        </div>
                        <Grid style={{position:"relative", width:"100%", height:"90%",top:"2%"}}>
                            <div className="NavLinks1">
                                <Btn variant="text" startIcon={<Avatar src={m_Notation} style={{height:"25px", width:"25px"}}/>} 
                                onClick={(event) =>{
                                    event.target.style.backgroundColor="#B93F2F";
                                }} style={{

                                    fontSize:"1.4vw",
                                    fontWeight:"bolder",
                                    position:"relative ",
                                    borderRadius:"10",
                                    width:"100%",
                                    top:"1%",

                                }}>
                                    Top 100
                                </Btn>

                                <Btn variant="text" startIcon={<Avatar src={m_Album} style={{height:"25px", width:"25px"}}/>} 
                                onClick={(event) =>{
                                    event.target.style.background="#B93F2F"
                                }} style={{
                                            fontSize:"1.4vw",
                                            fontWeight:"bolder",
                                            position:"relative ",
                                            textAlign:"left",
                                            height:"13%",
                                            width:"100%",
                                            top:"1%",

                                }}>
                                    Albums
                                </Btn>

                                <Btn variant="text" //startIcon={ } 
                                onClick={(event) =>{
                                    event.target.style.background="#B93F2F";
                                }} style={{
                                    fontSize:"1.4vw",
                                    fontWeight:"bolder",
                                    position:"relative ",
                                    borderRadius:"10",
                                    width:"100%",
                                    top:"1%",
                                }}>
                                    Artists
                                </Btn>

                                <Btn variant="text" startIcon={<m_Notation/>} 
                                onClick={(event) =>{
                                    event.target.style.background="#B93F2F";
                                }} style={{
                                    fontSize:"1.4vw",
                                    fontWeight:"bolder",
                                    position:"relative ",
                                    borderRadius:"10",
                                    width:"100%",
                                    top:"1%",
                                }}>
                                    Tracks
                                </Btn>

                                <Btn variant="text" startIcon={<m_Notation/>} 
                                onClick={(event) =>{
                                    event.target.style.background="#B93F2F";
                                }} style={{
                                    fontSize:"1.4vw",
                                    fontWeight:"bolder",
                                    position:"relative ",
                                    borderRadius:"10",
                                    width:"100%",
                                    top:"1%",
                                }}>
                                    Genres
                                </Btn>

                                <Btn variant="text" startIcon={<m_Notation/>} 
                                onClick={(event) =>{
                                    event.target.style.background="#B93F2F";
                                }} style={{
                                    fontSize:"1.4vw",
                                    fontWeight:"bolder",
                                    position:"relative ",
                                    borderRadius:"10",
                                    width:"100%",
                                    top:"1%",
                                }}>
                                    Playlists
                                </Btn>

                                <Btn variant="text" startIcon={<m_Notation/>} 
                                onClick={(event) =>{
                                    event.target.style.background="#B93F2F";
                                }} style={{
                                    fontSize:"1.4vw",
                                    fontWeight:"bolder",
                                    position:"relative ",
                                    borderRadius:"10",
                                    width:"100%",
                                    top:"1%",
                                }}>
                                    Playing Queue
                                </Btn>
                            </div>
                            <div className="NavLinks2">

                            </div>
                        </Grid>
                    </Grid>
                </Grid>
                <Grid className="contentCarrier">

                </Grid>

           </div>
       </div>
    );
}
export default Home;
